import './button-component.css';
import { useState } from 'react';

const [ clickCount, setClickCount ] = useState();

const changeValue = (e) => {
	e.preventDefault();
	let counter = 0;

	let selectedValue = e.target;
	if (clickCount / 2 === 0) selectedValue = 'O';
	if (clickCount / 2 !== 0) selectedValue = 'X';
	setClickCount(counter);
};

const ButtonComponent = () => {
	return <button className="btn-frame" onClick={changeValue} />;
};

export default ButtonComponent;
