import './button-component.css';
import { useState } from 'react';

const [ clickCount, setClickCount ] = useState();

const updateClick = (e) => {
	setClickCount(clickCount + 1);
	return clickCount;
};

const changeValue = (e) => {
	e.preventDefault();
	updateClick;
	let changeValue = e.target;
	if (changeValue / 2 === 0) changeValue = 'O';
	if (changeValue / 2 !== 0) changeValue = 'X';
	console.log(changeValue);
};

const ButtonComponent = () => {
	return <button className="btn-frame" onClick={changeValue} />;
};

export default ButtonComponent;
